angular.module("adamczak.strings",[]).service("stringService",function(){this.trim=function(value){return value?value.replace(/^\s+|\s+$/,""):void 0},this.prefix=function(value,prefix){return value&&this.trim(value).length>0?prefix+value:value},this.suffix=function(value,suffix){return value&&this.trim(value).length>0?value+suffix:value}}).directive("wrapvalue",function(stringService){return{restrict:"A",scope:{value:"=wrapvalue"},link:function(scope,elem){scope.$watch("value",function(newVal){var prefixed=stringService.prefix(newVal,elem.attr("prefix")||""),suffixed=stringService.suffix(prefixed,elem.attr("suffix")||"");elem.text(suffixed)})}}}).filter("truncate",function(stringService){return function(value,maxLength,suffix){return maxLength=maxLength||25,suffix=suffix||"",value.length>maxLength?stringService.suffix(value.substring(0,maxLength),suffix):value}});